package day03_variables_dataTypes;

public class constantVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double pi = 3.14;
		
		pi = 5;
		
		System.out.println(pi);
		
		final String s2 = " Guzal "; // make it constant variable, no one can change.
		
		
				
		System.out.println(s2);
		
		String name = "Guzal"; 
		int number = 3;
		boolean status = true;
		
		System.out.println("My name is " + name + ". I like " +number +". I can be good: " + status  + ".");
		
		
		
				

	}

}
