package day12_controlFlowStatement;

public class guzal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int row = 1; row < 3; row++) {
			for (int column = 1; column < 5; column++) {
				System.out.print("*");
			}
			System.out.println();
		}

		for (int i = 1; i < 3; i++) {
			for (int j = 1; j < 5; j++) {
				System.out.print("#");
			}
			System.out.println();
		}
		
		
		
		
	}

}
