package day30_wrapperClass;

public class MathClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		final double PI = Math.PI; // 3.14
		System.out.println(PI);

		System.out.println(Math.round(4.6)); // round to 5

		System.out.println(Math.round(4.5)); // round to 4

		System.out.println(Math.floor(4.9)); // pushing down limit 4

		System.out.println(Math.ceil(4.2)); // pushing upper limit 5
	}

}
